            //Para hacer el calculo del monto
            oReserva.monto = vehiculo.precio * dia;

            if(validarRegistro == null){
                var crear = await reservas.AddReserves(oReserva);
                var rs = js.InvokeAsync<object>("msjAlert", "Registrado Exitoso!", "success");
